import {
  Box,
  Button,
  CardContent,
  CircularProgress,
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  TablePagination
} from "@material-ui/core";
import React, { useEffect, useState } from "react";
import { listUsers } from "../service/UserService";

export const AppList = () => {
  const [users, setUsers] = useState([]);
  const [err, setErr] = useState("");

  useEffect(() => {
    listUsers("/users")
      .then(({ data }) => {
        setUsers(data);
      })
      .catch(e => {
        setErr(e.message);
      });
  }, []);

  return (
    <div>
      {err}
      <table>
        <tr>
          <th>
            <h3>Usuarios</h3>
            {users.map(user => (
              <div>{user.username}</div>
            ))}
          </th>

          <th>
            <h3>Email</h3>
            {users.map(user => (
              <div>{user.email}</div>
            ))}
          </th>
        </tr>
      </table>

      <Box component='div' my={1}>
            <CardContent>
                {falhou && (
                    <Box component={Alert} mb={2} severity='error'>
                        Um erro impediu o carregamento da lista de usuários!
                    </Box>
                )}
                <TableContainer component={Paper}>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Id</TableCell>
                                <TableCell>Usuário</TableCell>
                                <TableCell>Telefone</TableCell>
                                <TableCell>Endereço</TableCell>
                                <TableCell align='right'>Opções</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {atualizando && (
                                <TableRow key='atualizando'>
                                    <TableCell colSpan={5}>
                                        <CircularProgress disableShrink />
                                    </TableCell>
                                </TableRow>
                            )}
                            {usuarios.slice(pagina * tamanhoPagina, pagina * tamanhoPagina + tamanhoPagina).map(row => (
                                <TableRow key={row.id}>
                                    <TableCell component='th' scope='row'>
                                        {row.id}
                                    </TableCell>
                                    <TableCell>{row.username}</TableCell>
                                    <TableCell>{row.phoneNumber}</TableCell>
                                    <TableCell>{row.address}</TableCell>
                                    <TableCell align='right'>
                                        <Button color='secondary' onClick={() => aoExcluirUsuario(row)}>Excluir</Button>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
                <TablePagination
                    rowsPerPageOptions={[5, 10, 15, 25]}
                    component="div"
                    count={usuarios.length}
                    rowsPerPage={tamanhoPagina}
                    page={pagina}
                    onChangePage={aoMudarPagina}
                    onChangeRowsPerPage={aoMudarTamanhoPagina}
                />
            </CardContent>
        </Box>
    </div>
  );
};
